import React from "react";

class TodoItem extends React.Component {
    render() {
        console.log(this.props.todo.map( todo => {
            return todo.title
        }))


        const user_todo = this.props.todo.map( todo => {
            return todo
        })
        console.log("sa",user_todo.title)
        return (

            <li className="todo-item">
                <input
                    type="checkbox"
                    checked={user_todo.completed}
                    onChange={() => this.props.handleChange(user_todo.id)}
                />
                <span className={user_todo.completed ? "completed" : null}>
                    {user_todo.title}
                </span>
                <button className="btn-style" onClick={() => this.props.deleteTodo(user_todo.id)}> X </button>
            </li>
        );

    }
}
export default TodoItem;
