import React, { useState, useEffect } from "react";
import Todos from "./Todos";
import Header from "../components/layout/Header";
import AddTodo from "./AddTodo"

//khai báo thư viện axios
import axios from "axios";
import todos from "./Todos";


function TodoApp() {
    const id_user = 2;
    const [state, setState] = useState({
        todos: {}
    });

    const handleCheckboxChange = id => {
        if (state.todos.id == id){
            setState({
                todos: {...state.todos,completed:true}
            });
        }
    };
    const addTodo = title => {
        const todoData = {
            user: id_user,
            title: title,
            completed: false
        }
        axios.post("https://profile-json-server.herokuapp.com/todos", todoData);

    };
    const deleteTodo = id => {
        axios.delete(`https://server1todo.herokuapp.com/todos/${id}`)

    };
    useEffect(() => {
        const config = {
            params: {
                _limit: 5
            }

        }
        // tạo GET request để lấy danh sách todos
        axios.get(`https://profile-json-server.herokuapp.com/todos`)
            .then(response => {
                response.data.map( current_user =>{
                    if (current_user.user === 2){
                        setState({todos: [current_user]})
                    }
                })
            });

    }, []);

    console.log(state.todos)
    return (
        <div className="container">
            <Header />
            <AddTodo addTodo={addTodo} />
            <Todos todos={state.todos}
                   handleChange={handleCheckboxChange}
                   deleteTodo={deleteTodo} />
        </div>
    );

}
export default TodoApp;
