import React, { useState, useEffect } from "react";
import Todos from "./Todos";
import Header from "../components/layout/Header";
import AddTodo from "./AddTodo"

//khai báo thư viện axios
import axios from "axios";

function TodoApp() {
    const id_user = 2;
    const [state, setState] = useState({
        todos: []
    });

    const handleCheckboxChange = id => {
        setState({
            todos: state.todos(todo => {
                if (todo.id === id) {
                    todo.completed = !todo.completed;
                }
                return todo;
            })
        });
    };
    const addTodo = title => {
        const todoData = {
            title: title,
            completed: false
        }
        axios.post("https://server1todo.herokuapp.com/todos", todoData)
            .then(response => {
                console.log(response.data)
                setState({
                    todos: [...state.todos, response.data]
                })
            });
    };
    const deleteTodo = id => {
        axios.delete(`https://server1todo.herokuapp.com/todos/${id}`)
            .then(reponse => setState({
                todos: [
                    ...state.todos.filter(todo => {
                        return todo.id !== id;
                    })
                ]
            }))
    };
    useEffect(() => {
        const config = {
            params: {
                _limit: 5
            }

        }
        // tạo GET request để lấy danh sách todos
        axios.get(`https://profile-json-server.herokuapp.com/todos`)
            .then(response => {
                response.data.map( current_user =>{
                    if (current_user.id === 2){
                        setState({todos: current_user})
                    }
                })
            });
    }, []);

    return (
        <div className="container">
            <Header />
            <AddTodo addTodo={addTodo} />
            <Todos todos={state.todos}
                   handleChange={handleCheckboxChange}
                   deleteTodo={deleteTodo} />
        </div>
    );

}
export default TodoApp;
