import React, { useState, useEffect } from "react";
import Todos from "./Todos";
import Header from "../components/layout/Header";
import AddTodo from "./AddTodo"

//khai báo thư viện axios
import axios from "axios";

function TodoApp() {

    const [state, setState] = useState({
        todos: []
    });
    const handleCheckboxChange = id => {
        setState({
            todos: state.todos.map(todo => {
                if (todo.id === id) {
                    todo.completed = !todo.completed;
                }
                return todo;
            })
        });
    };
    const deleteTodo = id => {
        axios.delete(`https://server1todo.herokuapp.com/users/${id}`)
            .then(reponse => setState({
                todos: [
                    ...state.todos.filter(todo => {
                        return todo.id !== id;
                    })
                ]
            }))
    };

    const addTodo = title => {
        const todoData = {
            title: title,
            completed: false
        }
        axios.post("https://jsonplaceholder.typicode.com/todos", todoData)
            .then(response => {
                console.log(response.data)
                setState({
                    todos: [...state.todos, response.data]
                })
            });
    };

    useEffect(() => {
        const config = {
            params: {
                _limit: 5
            }

        }
        // tạo GET request để lấy danh sách todos
        axios.get(`https://server1todo.herokuapp.com/users`, config)
            .then(response => setState({ todos: response.data }));
    }, []);
    console.log(state.todos.filter(todo =>{
        return todo.id === 2
    }))
    return (
        <div className="container">
            <Header />
            <AddTodo addTodo={addTodo} />
            <Todos todos={state.todos}
                handleChange={handleCheckboxChange}
                deleteTodo={deleteTodo} />
        </div>
    );

}
export default TodoApp;
