import React, { useState, useEffect } from "react";
import Todos from "./Todos";
import Header from "../components/layout/Header";
import AddTodo from "./AddTodo"

//khai báo thư viện axios
import axios from "axios";

function TodoApp() {

    const [state, setState] = useState({
        todos: []
    });


    useEffect(() => {
        const config = {
            params: {
                _limit: 5
            }

        }
        // tạo GET request để lấy danh sách todos
        axios.get(`https://server1todo.herokuapp.com/users`, config)
            .then(response => setState({ todos: response.data }));
    }, []);
    const user = state.todos.filter( todo =>{
        return todo.id === 2
    })

    console.log("âcsca",user.map(note => {
        return note.address
    }))
    return (
        <div className="container">
            <Header />
            <AddTodo  />
            <Todos todos={user}
                />
        </div>
    );

}
export default TodoApp;
